    <script>


        var users;

        $("#team_members").change(function(){
            $('#diverge').hide();
            $('#reg').show();
            var formcount=document.getElementById('team_members').value;
            document.getElementById('forms').innerHTML='';

            for(var i=1;i<=formcount;i++){
                document.getElementById('forms').innerHTML+=new_old(i)+new_email(i);
            }
            // var register = '<input type="submit" value="Register" class="btn btn-default" id="reg">';

            // var contact = '';


            $("#imp").css("display", "block");


            // document.getElementById('forms').innerHTML+=contact+register;

        });

        function create_forms(){
            var formcount=document.getElementById('team_members').value;
            document.getElementById('forms').innerHTML='';
            for(var i=1;i<=formcount;i++){

                document.getElementById('forms').innerHTML+=new_old(i)+new_email(i);
                // var register = '<input type="submit" value="Register" class="btn btn-default" id="reg">';

                // var contact = '<p class="college_text">Please <a href="http://www.techfest.org/contactus" target="_blank">contact us</a> if you face any difficulties in registration.</p></div>';
                $("#imp").css("display", "block");

                // document.getElementById('forms').innerHTML+=contact+register;
            }
        }

        function new_old(n){
            var compi = "{{ $compi_name }}";
            if(n==1 || n=='1'){
                var zonal_city='<div class="form-group"><label class="control-label col-md-1 col-sm-2" for="zonal_city">Zonal City:</label><div class="col-md-5 col-sm-10"><select class="form-control" id="zonal_city" name="zonal_city"  required><option value="">Select City</option><option value="2">Bhopal</option><option value="3">Hyderabad</option><option value="4">Jaipur</option><option value="5">Mumbai</option></select></div></div>';
                var heading='<h3 class="team_leader" style="color: #cc6600">Team Leader</h3><br />';
            }
            else{
                var heading='<h3 class="team_leader" style="color: #cc6600">Participant '+n+'</h3><br />';
            }
            var new_old='<input type="radio" class="form-control" id="user' + n + '1" name="user' + n + '" style="display:none" checked="checked" onclick="replace(' + n + ',1)" />';
            var div_open='<div id="section' + n + '">';

            if (compi == "vise_clutch" || compi == "meshmerize" || compi == "enigma"){
                if(n==1 || n=='1'){
                    return zonal_city+heading+new_old+div_open; }
                else { return heading+new_old+div_open; }
            }
            else {
                return heading+new_old+div_open;
            }
        }

        function exist_email(n){
            var email='<input type="email" name="emailid' + n + '" required="required" placeholder="Email Id" id="email' + n + '" class="old" title="Email Id" size="30" />';
            return email;
        }


        function new_email(n) {
            var firstname='<div class="form-group"><label class="control-label col-md-1 col-sm-2" for="name">First Name:</label><div class="col-md-5 col-sm-10"><input type="text" class="form-control" name="firstname' + n + '" required="required" placeholder="First Name" id="fn" pattern="[A-Za-z ]{1,70}" title="First Name: Only Letters" size="30"/></div></div>';

